<?php foreach ($games as $k => $item) : ?>
    <?php
    // The present time
    $t = time();
    // convert time (ex: 19-02-2017 22:11:46) => seconds(s)
    $item_time = strtotime($item->publish_date);
    // time date 3day: (The present time - time game new creat) < 3 date(= s*i*h*3day)
    $time = (($t - $item_time) < 60 * 60 * 24 * 3);
    ?>
    <div class="carousel-item">
        <a class="carousel-entity" href="/<?php echo $item->slug; ?>" title="<?php echo $item->name; ?>">
            <?php if ($time == true) {
                echo $update_svg;
            } elseif ($item->is_hot == "yes") {
                echo $hot_svg;
            } elseif ($item->is_new == "yes") {
                echo $new_svg;
            } ?>
            <img class="carousel-img" src="<?php echo \helper\image::get_thumbnail($item->image, 174, 100, 'f'); ?>" width="174" height="100" alt="<?php echo $item->name; ?>" title="<?php echo $item->name; ?>">
            <span class="carousel-showtitle">
                <?php echo $item->name; ?>
            </span>
        </a>
    </div>
<?php endforeach; ?>